INSERT INTO SICBA.Catalogo.Subfamilias (iIdSubfamilia, iIdFamilia, iNumeroCuenta, sNombre, sDescripcion, dValorRecuperable, bActivo, dtFechaCreacion, dtFechaModificacion) VALUES (136, 1300, 6, N'ASIGNACIONES DE TÉCNICO, DE MANDO, POR COMISIÓN, DE VUELO Y DE TÉCNICO ESPECIAL.', N'REMUNERACIONES A LOS MIEMBROS DEL EJÉRCITO, FUERZA AÉREA Y ARMADA NACIONALES, TITULADOS EN PROFESIONES DE LOS DISTINTOS SERVICIOS MILITARES, POR EL DESEMPEÑO DE COMISIONES DENTRO DEL RAMO Y QUE PERTENEZCAN A LA MILICIA PERMANENTE; REMUNERACIONES A GENERALES, JEFES Y OFICIALES INVESTIDOS CONFORME A LAS LEYES Y ORDENANZAS DEL MANDO MILITAR, DE UNA CORPORACIÓN DEL EJÉRCITO O DE UNA UNIDAD DE LA ARMADA. SU CUOTA NO PODRÁ VARIAR DURANTE EL EJERCICIO FISCAL RESPECTIVO. REMUNERACIONES A LOS MIEMBROS DEL EJÉRCITO Y LA ARMADA POR EL DESEMPEÑO DE UNA COMISIÓN QUE NO SEA LA PROPIA DE SU CARGO, COMO EN LOS ESTADOS MAYORES DE LOS SECRETARIOS Y SUBSECRETARIOS, AYUDANTÍA DEL OFICIAL MAYOR Y JEFES DE SECCIÓN DE LOS DIVERSOS DEPARTAMENTOS DE LA SECRETARÍA DE LA DEFENSA NACIONAL Y AYUDANTÍA DE LOS FUNCIONARIOS SUPERIORES DE LA SECRETARÍA DE MARINA; REMUNERACIONES A LOS MIEMBROS DEL EJÉRCITO Y LA ARMADA, QUE HABITUALMENTE DESEMPEÑAN SERVICIOS EN UNIDADES AÉREAS DE LAS FUERZAS ARMADAS MEXICANAS REMUNERACIONES COMPLEMENTARIAS A LOS HABERES DE LOS GENERALES DEL EJÉRCITO Y FUERZA AÉREA, ASÍ COMO DE LOS ALMIRANTES DE LA ARMADA QUE SEAN AUTORIZADAS POR EL TITULAR DEL RAMO Y LAS QUE ÉSTE MISMO AUTORICE EN CASOS ESPECIALES PARA LOS JEFES Y OFICIALES DEL EJÉRCITO Y FUERZA AÉREA, CAPITANES Y OFICIALES DE LA ARMADA.', 0, 1, N'2020-04-03 00:47:39.050', N'2020-04-03 00:47:39.050');
