INSERT INTO SICBA.Catalogo.Subfamilias (iIdSubfamilia, iIdFamilia, iNumeroCuenta, sNombre, sDescripcion, dValorRecuperable, bActivo, dtFechaCreacion, dtFechaModificacion) VALUES (385, 3800, 5, N'GASTOS DE REPRESENTACIÓN', N'ASIGNACIONES DESTINADAS A CUBRIR GASTOS AUTORIZADOS A LOS SERVIDORES PÚBLICOS DE MANDOS MEDIOS Y SUPERIORES POR CONCEPTO DE ATENCIÓN A ACTIVIDADES INSTITUCIONALES ORIGINADAS POR EL DESEMPEÑO DE LAS FUNCIONES ENCOMENDADAS PARA LA CONSECUCIÓN DE LOS OBJETIVOS DE LOS ENTES PÚBLICOS A LOS QUE ESTÉN ADSCRITOS.', 0, 1, N'2020-04-03 00:47:39.140', N'2020-04-03 00:47:39.140');
INSERT INTO SICBA.Catalogo.Subfamilias (iIdSubfamilia, iIdFamilia, iNumeroCuenta, sNombre, sDescripcion, dValorRecuperable, bActivo, dtFechaCreacion, dtFechaModificacion) VALUES (391, 3900, 1, N'SERVICIOS FUNERARIOS Y DE CEMENTERIOS', N'ASIGNACIONES DESTINADAS A CUBRIR SERVICIOS Y PAGOS DE DEFUNCIÓN COMO TRASLADO DE CUERPOS, VELACIÓN, APOYO PARA TRÁMITES LEGALES, CREMACIÓN Y EMBALSAMAMIENTO Y ATAÚDES, A LOS FAMILIARES DE SERVIDORES PÚBLICOS, CIVILES Y MILITARES AL SERVICIO DE LOS ENTES PÚBLICOS, ASÍ COMO DE PENSIONISTAS DIRECTOS, CUYO PAGO ES CON CARGO AL ERARIO, A EXCEPCIÓN DE LOS MIEMBROS DEL SERVICIO EXTERIOR QUE PEREZCAN FUERA DEL PAÍS. ASIMISMO, CON CARGO A ESTA PARTIDA SE CUBRIRÁN APOYOS A LOS MILITARES EN ACTIVO O RETIRADOS PARA GASTOS DE SEPELIO EN CASO DE FALLECIMIENTO DE SUS DEPENDIENTES ECONÓMICOS. INCLUYE LOS GASTOS POR CONCEPTO DE HONORES PÓSTUMOS A QUIENES POR SUS MÉRITOS O SERVICIOS SE CONSIDERE CONVENIENTE TRIBUTAR; GASTOS DE INHUMACIÓN DE LOS ALUMNOS INTERNOS EN LAS ESCUELAS DE LA FEDERACIÓN Y, EN LOS CASOS DE QUE LOS CUERPOS NO SEAN RECLAMADOS, DE LOS MILITARES QUE FALLEZCAN EN PRISIÓN CUMPLIENDO SENTENCIA CONDENATORIA.', 0, 1, N'2020-04-03 00:47:39.143', N'2020-04-03 00:47:39.143');
INSERT INTO SICBA.Catalogo.Subfamilias (iIdSubfamilia, iIdFamilia, iNumeroCuenta, sNombre, sDescripcion, dValorRecuperable, bActivo, dtFechaCreacion, dtFechaModificacion) VALUES (392, 3900, 2, N'IMPUESTOS Y DERECHOS', N'ASIGNACIONES DESTINADAS A CUBRIR LOS IMPUESTOS Y/O DERECHOS QUE CAUSE LA VENTA DE PRODUCTOS Y SERVICIOS AL EXTRANJERO, GASTOS DE ESCRITURACIÓN, LEGALIZACIÓN DE EXHORTOS NOTARIALES, DE REGISTRO PÚBLICO DE LA PROPIEDAD, TENENCIAS Y CANJE DE PLACAS DE VEHÍCULOS OFICIALES, DILIGENCIAS JUDICIALES; DERECHOS Y GASTOS DE NAVEGACIÓN, DE ATERRIZAJE Y DESPEGUE DE AERONAVES, DE VERIFICACIÓN, CERTIFICACIÓN, Y DEMÁS IMPUESTOS Y DERECHOS CONFORME A LAS DISPOSICIONES APLICABLES. EXCLUYE IMPUESTOS Y DERECHOS DE IMPORTACIÓN.', 0, 1, N'2020-04-03 00:47:39.143', N'2020-04-03 00:47:39.143');
