INSERT INTO SICBA.Catalogo.Subfamilias (iIdSubfamilia, iIdFamilia, iNumeroCuenta, sNombre, sDescripcion, dValorRecuperable, bActivo, dtFechaCreacion, dtFechaModificacion) VALUES (395, 3900, 5, N'PENAS, MULTAS, ACCESORIOS Y ACTUALIZACIONES', N'ASIGNACIONES DESTINADAS A CUBRIR LAS EROGACIONES DERIVADAS DEL PAGO EXTEMPORÁNEO DE PASIVOS FISCALES, ADEUDOS U OBLIGACIONES DE PAGO, COMO MULTAS, ACTUALIZACIONES, INTERESES Y DEMÁS ACCESORIOS POR DICHOS PAGOS. INCLUYE LOS GASTOS FINANCIEROS POR PAGO EXTEMPORÁNEO DE ESTIMACIONES Y DE AJUSTE DE COSTOS DE OBRA PÚBLICA, ASÍ COMO LOS GASTOS NO RECUPERABLES DERIVADOS DE LA TERMINACIÓN ANTICIPADA DE CONTRATOS DE ADQUISICIONES U OBRAS PÚBLICAS. EXCLUYE CAUSAS IMPUTABLES A SERVIDORES PÚBLICOS.', 0, 1, N'2020-04-03 00:47:39.147', N'2020-04-03 00:47:39.147');
INSERT INTO SICBA.Catalogo.Subfamilias (iIdSubfamilia, iIdFamilia, iNumeroCuenta, sNombre, sDescripcion, dValorRecuperable, bActivo, dtFechaCreacion, dtFechaModificacion) VALUES (396, 3900, 6, N'OTROS GASTOS POR RESPONSABILIDADES', N'ASIGNACIONES DESTINADAS A CUBRIR LAS EROGACIONES DE LOS ENTES PÚBLICOS QUE DERIVEN DEL ROBO O EXTRAVÍO DE RECURSOS PÚBLICOS QUE NO SEAN RECUPERABLES E IMPLIQUEN AFECTAR SU PRESUPUESTO DISPONIBLE. INCLUYE EROGACIONES DE LOS ENTES PÚBLICOS QUE SE DERIVEN DE LA RESPONSABILIDAD CIVIL, MONTOS DIFERENCIALES DE LAS INDEMNIZACIONES QUE NO CUBRAN LAS SUMAS ASEGURADAS, LOS IMPORTES DEDUCIBLES DEL SEGURO DE RESPONSABILIDAD PATRIMONIAL DEL ESTADO ASÍ COMO AQUELLAS EROGACIONES DISTINTAS DE LAS CONSIDERADAS EN LAS DEMÁS PARTIDAS DE ESTE CONCEPTO, QUE IMPLIQUEN AFECTAR EL PRESUPUESTO DISPONIBLE DEL ENTE PÚBLICO. EXCLUYE LAS RECUPERACIONES DE RECURSOS QUE SE REALICEN POR LOS DIVERSOS MEDIOS ESTABLECIDOS POR LAS DISPOSICIONES APLICABLES, COMO ES EL FONDO DE GARANTÍA PARA REINTEGROS AL ERARIO EN EL CASO DE LOS ENTES PÚBLICOS.', 0, 1, N'2020-04-03 00:47:39.147', N'2020-04-03 00:47:39.147');
